import * as moment from "moment";
import "moment-duration-format";

export function sumTime(time1:any, time2:any){

    return moment.duration(time1).add(moment.duration(time2)).format('hh:mm:ss:ms',{trim:false});
    //trim: false prevents trimming of zero values for large units

    //return informat = 'hh:mm:ss.SSS';

}
