import React, { FC, useState } from "react";
import { ClipTypeObj } from "../../types/ClipTypes";
import { DecideReelTypeProp } from "../../types/ReelTypes";

const DecideReel: FC = () => {
  const [selectDefinition, setSelectDefiniton] = useState<String>();
  const [reelName, setReelName] = useState("");

  const buttonHandler = (event: React.ChangeEvent<HTMLInputElement>) => {
    setSelectDefiniton(event.target.value);
    console.log(selectDefinition);
  };

  const nameHandler = (event: React.ChangeEvent<HTMLInputElement>) =>{
    setReelName(event.target.value);
    console.log('Name is: ', event.target.value)
  }
  return (
    <div className="group">
      <label>
        Name: <input type="text" name="name" onChange={nameHandler} value={reelName}/>
      </label>
      <div>
        Definition:
        <p>
          <input type="radio" name="def" value="PAL" onChange={buttonHandler} />
          <label htmlFor="pal">PAL</label>
        </p>
        <p>
          <input
            type="radio"
            name="def"
            value="NTSC"
            onChange={buttonHandler}
          />
          <label htmlFor="ntsc">NTSC</label>
        </p>
      </div>
    </div>
  );
};

export default DecideReel;
