import moment from "moment";
import "moment-duration-format";

function convertTime(time:any){
    const convertedTime = time.split(":");
    const newTime = convertedTime[0]+":"+convertedTime[1]+":"+convertedTime[2]+"."+convertedTime[3];
    return newTime;
}

export function sumTime(time1:any, time2: any){
    var newTime1 = moment(convertTime(time1), 'hh:mm:ss.SSS');
    var newTime2 = moment.duration(convertTime(time2)).asMilliseconds();
    var addedTime = newTime1.add(newTime2, 'milliseconds').format('hh:mm:ss.SSS');

    return addedTime;
    // return moment.duration(convertTime(time1)).add(moment.duration(convertTime(time2))).format('hh:mm:ss.SSS', {trim: false});    
    
}

export function diffTime(time1:any, time2: any){
    var newTime1 = moment(convertTime(time1), 'hh:mm:ss.SSS');
    var newTime2 = moment(convertTime(time2), 'hh:mm:ss.SSS');

    return moment.duration(newTime1.diff(newTime2)).format('hh:mm:ss.SSS', {trim: false});    

}
