import React, { FC, useState } from "react";
import RadioButton from "./RadioButton";
import Button from "react-bootstrap/Button";
import "./DecideReel.css";

const DecideReel: FC = () => {
  const [reelName, setReelName] = useState("");

  const nameHandler = (event: React.ChangeEvent<HTMLInputElement>) =>{
    setReelName(event.target.value);
    console.log('Name is: ', event.target.value)
  }

  const nav = useNavigate();
  const handleSelectClips = () => {
    nav("/select-clips");
  };

  return (
    <div className="container">
      <div className="name-container">
        Name: <input type="text" name="name" onChange={nameHandler} value={reelName}/>
      </div>
      <div className="radio-container">
      <RadioButton/>
      </div>
      <Button onClick = {handleSelectClips}>Select Clips</Button>
    </div>
  );
};

export default DecideReel;
