import React, { useState } from "react";
import "./RadioButton.css";

const RadioButton = () => {
  // The selected drink
  const [selectDefinition, setSelectDefiniton] = useState<String>();

  // This function will be triggered when a radio button is selected
  const defintiionHandler = (event: React.ChangeEvent<HTMLInputElement>) => {
    setSelectDefiniton(event.target.value);
  };

  return (
    <div className="container">
      <fieldset>
        <legend>Please select definition?</legend>
        <p>
          <input
            type="radio"
            name="definition"
            value="PAL"
            id="pal"
            onChange={defintiionHandler}
          />
          <label htmlFor="pal">PAL</label>
        </p>

        <p>
          <input
            type="radio"
            name="definition"
            value="NTSC"
            id="ntsc"
            onChange={defintiionHandler}
          />
          <label htmlFor="ntsc">NTSC</label>
        </p>
      </fieldset>

      {/* Display the selected drink */}
      {selectDefinition && <h2>{selectDefinition}</h2>}
    </div>
  );
};

export default RadioButton;
